mapper.select:
select faq_id, fk_userid, subject, content, regDate, status, faq_cate_name from ( select faq_id, fk_userid, C.faq_cate_name, fk_category, subject, content, regDate, status from yes_faq F join yes_faq_cate C on F.fk_category = C.faq_cate_code ) T where 1=1
and fk_category = #{category}
and subject like '%' || #{searchWord} || '%'
