mapper.select:
select prod_id, prod_img, prod_title, info_grade, info_run_time, map_name 
from view_rev_showInfo
where prod_id = #{showNum}

select date_id, prod_id, seattype_id, seat_type, seat_name, seat_price, seat_status, date_id, seat_color
from view_seat_info
where prod_id = #{showNum}

select seat_type, seat_price, seat_color
from yes_seat_type
where prod_id = #{showNum}

select date_id
from yes_show_date
where prod_id = #{showNum} and to_char(date_showday, 'yy/mm/dd') || ' ' || to_char(date_showday, 'day') = #{showDay} and date_showtime = #{showRound}

select faq_id, fk_userid, subject, content, regDate, status, faq_cate_name from ( select faq_id, fk_userid, C.faq_cate_name, fk_category, subject, content, regDate, status from yes_faq F join yes_faq_cate C on F.fk_category = C.faq_cate_code ) T where 1=1
and fk_category = #{category}
and subject like '%' || #{searchWord} || '%'

select notice_id, fk_userid,no_cate_name,category,ticketopenday,subject,readCount,regDate,status,fileName,orgFilename,fileSize from ( select row_number() over(order by ${order} desc) AS rno, notice_id,fk_userid,no_cate_name,category,ticketopenday,subject,readCount,regDate,status,fileName,orgFilename,fileSize from ( select notice_id,fk_userid,no_cate_name,category,nvl(ticketopenday, ' ') as ticketopenday,subject,readCount,regDate,status,fileName,orgFilename,fileSize from yes_notice N join yes_notice_cate C on N.category = C.no_cate_code order by notice_id desc ) where status = 1
and subject like '%' || #{searchWord} || '%'
)V where rno between #{startRno} and #{endRno}

select previousseq, previoussubject , notice_id,fk_userid,category, ticketopenday, subject,readCount , regDate , fileName,orgFilename,fileSize , nextseq, nextsubject from ( select lag(notice_id, 1) over(order by notice_id desc) as previousseq , lag(subject, 1) over(order by notice_id desc) as previoussubject
, notice_id, fk_userid, category, subject, readCount , nvl(ticketopenday, ' ') as ticketopenday , to_char(regDate, 'yyyy-mm-dd') as regDate , status, fileName, orgFilename, fileSize
, lead(notice_id, 1) over(order by notice_id desc) as nextseq , lead(subject, 1) over(order by notice_id desc) as nextsubject from yes_notice where status = 1 ) V where notice_id = #{seq}

	select RNO, prod_id, category_name, fk_category_id, prod_title, prod_img, date_start, date_end, info_open_date, info_close_date, info_rev_status, info_qnty, map_address, map_id
	from (
    	select ROW_NUMBER() OVER (ORDER BY prod_id desc) as RNO, prod_id, category_name, fk_category_id, prod_title, prod_img, date_start, date_end, info_open_date, info_close_date, info_rev_status, info_qnty, map_address, map_id
    	from (
        	select P.prod_id, C.category_name, fk_category_id, prod_title, prod_img, date_start, date_end, info_open_date, info_close_date, info_rev_status, info_qnty, map_address, M.map_id, status
        	from prod P join yes_show_category C
        	on P. fk_category_id = C.category_id
        	left join yes_show_map M
        	on P.prod_id = M. prod_id
        	order by P.prod_id
    	) V
    	where status = 1 

       	and lower(prod_title) like '%' || lower(#{searchWord}) || '%'

	) T
	where rno between #{startRno} and #{endRno}

select userid 
from yes_member
where status = 1 and userid = #{userid}

mapper.update:
update yes_member
set kakaoStatus = '1'
where status = 1 and email = #{email}

update yes_member
set naverStatus = '1'
where status = 1 and email = #{email}

mapper.select:
select userid, name, postcode, address, detailaddress, hp1, hp2, hp3, email, isSMS, isEMAIL
from yes_member
where idx = #{idx}

mapper.update:
update yes_member

set name = #{name}, email = #{email}, hp1 = #{hp1}, hp2 = #{hp2}, hp3 = #{hp3}, postcode = #{postcode}, address = #{address}, detailAddress = #{detailAddress}, isSMS = #{isSMS}, isEMAIL = #{isEMAIL}


set name = #{name}, pwd = #{pwd}, email = #{email}, hp1 = #{hp1}, hp2 = #{hp2}, hp3 = #{hp3}, postcode = #{postcode}, address = #{address}, detailAddress = #{detailAddress}, isSMS = #{isSMS}, isEMAIL = #{isEMAIL}

where idx = #{idx}

update yes_member 
set sessionKey = #{sessionId}, sessionLimit = #{sessionLimit}
where status = 1 and userid = #{userid}

update yes_member
set status = 0
where idx = #{idx} and pwd = #{pwd}
